import React from "react";
import PropTypes from "prop-types";
import "./Button.css";
import { getKeyBGC } from "./AppVars";

export default class Button extends React.Component {
  static propTypes = {
    name: PropTypes.string,
    grey: PropTypes.bool,
    wide: PropTypes.bool,
    clickHandler: PropTypes.func,
    bgc: PropTypes.string,
  };
  constructor(props) {
    super(props);
    this.state = {
      bgColor: "white",
      grey: false,
      white: true,
    }
  }

  handleClick = () => {
    this.props.clickHandler(this.props.name);
    if (this.state.bgColor !== getKeyBGC(this.props.name)) {
      alert("Button js: I am going to change color of button " + this.props.name + " now because state is " + this.state.bgColor + " and array is " + getKeyBGC(this.props.name) + ".")
      this.setState({
        bgColor: getKeyBGC(this.props.name),
        grey: getKeyBGC(this.props.name) === "grey",
        white: getKeyBGC(this.props.name) === "white",
      }); 
    }
  };

  render() {
    const className = [
      "component-button",
      this.props.grey ? "grey" : "",
      this.props.wide ? "wide" : "",
    ];

    return (
      <div className={className.join(" ").trim()}>
       <button 
          onClick={this.handleClick}>{this.props.name}
        </button>
      </div>
    );
  }
}
