import React from "react";
import PropTypes from "prop-types";
import AppState from "./AppState"

import "./Display.css";

export default class Display extends React.Component {
  static propTypes = {
    value: PropTypes.string,
  };
  state = {
    letters: [],
    colours: [],
  };

  constructor(props) {
    super(props);
    this.state = {
      bgColor: ""
    }
    for (var i=0; i<AppState.NUMTRIES; i++) {
      for (var j=0; j<AppState.WORDLEN; j++) {
        this.state.letters[i*AppState.WORDLEN + j] = "";
        this.state.colours[i*AppState.WORDLEN + j] = "black";
      }
    }
  }


  render() {
    return (
      <div className="component-display">
        <div>{this.props.value}</div>
      </div>
    );
  }
}
