import React from "react";
import DisplayRow from "./DisplayRow";
import ButtonPanel from "./ButtonPanel";
import calculate from "../logic/calculate";
import { activeRow, activeCol, displayChars, displayCols, initialiseState, nextCol, nextRow, WORDLEN, NUMTRIES } from "./AppVars";
import "./App.css";
import { check } from "prettier";

function endGame() {
    alert("Game Finished!")
}

export default class App extends React.Component {
  state = {
    numTries: 0,
  };

  constructor() {
    super();
    this.numTries = 0;
    initialiseState();
  };


  handleClick = buttonName => {
    var index = activeRow*WORDLEN + activeCol;
    if (buttonName === "<=") {
      if (activeCol > 0) {
        displayChars[index] = "";
        activeCol = activeCol - 1;
      }
      return;
    }
    if (buttonName === "Enter") {
      if (activeCol == WORDLEN-1) {
        if (checkWord() == 0) {
          alert("Invalid word")
          return;
        }
        else if (checkWord() == 1) {
          nextRow();
          if (activeRow == NUMTRIES) {
            endGame();
          }
        }
        else if (checkWord() == 2) {
          endGame();
        }
        this.setState({
          numTries: this.numTries + 1,
        }) 
      }
      return;
    }
    if (activeCol == WORDLEN-1) {
      return;
    }
    displayChars[index] = buttonName;
    nextCol();

    this.setState({
      numTries: this.numTries + 1,
    })
  };

  render() {
    return (
      <div className="component-app">
        <DisplayRow numTries = {this.numTries} />
        <ButtonPanel clickHandler={this.handleClick} />
      </div>
    );
  }
}
