import React from "react";
import PropTypes from "prop-types";
import "./DisplayBtn.css";

export default class DisplayBtn extends React.Component {
  static propTypes = {
    value: PropTypes.string,
    bgc: PropTypes.string,
    msg: PropTypes.string,
  };

  constructor(props) {
    super(props);
    this.state = {
      value: this.props.value,
      bgColor: this.props.bgc,
    }
  }

  render() {
    const className = [
      "display-button",
      this.props.orange ? "orange" : "",
      this.props.green ? "green" : ""
    ];
    {if (this.props.msg === "true") {alert("in DisplayBtn render " + this.state.value)}}
    return (
      <div className={className.join(" ").trim()}>
       <button style={{backgroundColor: this.state.bgColor}}> this.state.value </button>
      </div>
    );
  }
}
