import React from "react";
import DisplayRow from "./DisplayRow";
import ButtonPanel from "./ButtonPanel";
import calculate from "../logic/calculate";
import { activeRow, activeCol, displayChars, displayCols, initialiseState, nextCol, WORDLEN } from "./AppVars";
import "./App.css";

export default class App extends React.Component {
  state = {
    numTries: 0,
  };

  constructor() {
    super();
    numTries = 0;
    initialiseState();
  };

  handleClick = buttonName => {
    if (activeCol < WORDLEN) {
      displayChars[activeRow*WORDLEN + activeCol] = buttonName;
      displayCols[activeRow*WORDLEN + activeCol] = "orange";
      nextCol();
    }
    this.setState({numTries: numTries + 1})
  };

  render() {
    return (
      <div className="component-app">
        <DisplayRow />
        <ButtonPanel clickHandler={this.handleClick} />
      </div>
    );
  }
}
